package com.insurai.entity;
import jakarta.persistence.*; import java.time.LocalDateTime;
@Entity @Table(name="appointments") public class Appointment { @Id @GeneratedValue(strategy=GenerationType.IDENTITY) private Long id; @ManyToOne @JoinColumn(name="user_id") private User user; @ManyToOne @JoinColumn(name="agent_id") private User agent; @Column(length=2000) private String message; private LocalDateTime appointmentTime; private String status; public Appointment(){} public Long getId(){return id;} public void setId(Long id){this.id=id;} public User getUser(){return user;} public void setUser(User user){this.user=user;} public User getAgent(){return agent;} public void setAgent(User agent){this.agent=agent;} public String getMessage(){return message;} public void setMessage(String message){this.message=message;} public LocalDateTime getAppointmentTime(){return appointmentTime;} public void setAppointmentTime(LocalDateTime appointmentTime){this.appointmentTime=appointmentTime;} public String getStatus(){return status;} public void setStatus(String status){this.status=status;} }
