package com.insurai.mapper; import com.insurai.dto.AppointmentDto; import com.insurai.entity.Appointment; import org.springframework.stereotype.Component; @Component public class AppointmentMapper { public AppointmentDto toDto(Appointment a){ if(a==null) return null; AppointmentDto d=new AppointmentDto(); d.setId(a.getId()); d.setUserId(a.getUser()!=null?a.getUser().getId():null); d.setAgentId(a.getAgent()!=null?a.getAgent().getId():null); d.setMessage(a.getMessage()); d.setAppointmentTime(a.getAppointmentTime()); d.setStatus(a.getStatus()); d.setUserName(a.getUser()!=null?a.getUser().getName():null); d.setAgentName(a.getAgent()!=null?a.getAgent().getName():null); return d; } }
