package com.insurai.mapper; import com.insurai.dto.NotificationDto; import com.insurai.entity.Notification; import org.springframework.stereotype.Component; @Component public class NotificationMapper { public NotificationDto toDto(Notification n){ if(n==null) return null; NotificationDto d=new NotificationDto(); d.setId(n.getId()); d.setUserId(n.getUserId()); d.setAgentId(n.getAgentId()); d.setTitle(n.getTitle()); d.setBody(n.getBody()); d.setSeen(n.getSeen()); d.setCreatedAt(n.getCreatedAt()); return d; } }
