package com.insurai.mapper; import com.insurai.dto.PolicyDto; import com.insurai.entity.Policy; import org.springframework.stereotype.Component; @Component public class PolicyMapper { public PolicyDto toDto(Policy p){ if(p==null) return null; PolicyDto d=new PolicyDto(); d.setId(p.getId()); d.setTitle(p.getTitle()); d.setDescription(p.getDescription()); d.setPremium(p.getPremium()); return d; } public Policy toEntity(PolicyDto d){ if(d==null) return null; Policy p=new Policy(); p.setId(d.getId()); p.setTitle(d.getTitle()); p.setDescription(d.getDescription()); p.setPremium(d.getPremium()); return p; } }
