package com.insurai.mapper; import com.insurai.dto.UserDto; import com.insurai.entity.User; import org.springframework.stereotype.Component; @Component public class UserMapper { public UserDto toDto(User u){ if(u==null) return null; UserDto d = new UserDto(); d.setId(u.getId()); d.setName(u.getName()); d.setEmail(u.getEmail()); d.setRole(u.getRole()); d.setApproved(u.getApproved()); return d; } }
