package com.insurai.entity;
import jakarta.persistence.*; import java.time.LocalDateTime;
@Entity @Table(name="notifications") public class Notification { @Id @GeneratedValue(strategy=GenerationType.IDENTITY) private Long id; private Long userId; private Long agentId; private String title; @Column(length=2000) private String body; private Boolean seen=false; private LocalDateTime createdAt=LocalDateTime.now(); public Notification(){} public Long getId(){return id;} public void setId(Long id){this.id=id;} public Long getUserId(){return userId;} public void setUserId(Long userId){this.userId=userId;} public Long getAgentId(){return agentId;} public void setAgentId(Long agentId){this.agentId=agentId;} public String getTitle(){return title;} public void setTitle(String title){this.title=title;} public String getBody(){return body;} public void setBody(String body){this.body=body;} public Boolean getSeen(){return seen;} public void setSeen(Boolean seen){this.seen=seen;} public LocalDateTime getCreatedAt(){return createdAt;} public void setCreatedAt(LocalDateTime createdAt){this.createdAt=createdAt;} }
