package com.insurai.security; import com.auth0.jwt.JWT; import com.auth0.jwt.algorithms.Algorithm; import org.springframework.beans.factory.annotation.Value; import org.springframework.stereotype.Component; import java.util.Date; @Component public class JwtUtil { @Value("${jwt.secret}") private String secret; @Value("${jwt.expirationMs}") private Long expirationMs; public String generateToken(String subject){ Algorithm alg = Algorithm.HMAC256(secret.getBytes()); return JWT.create().withSubject(subject).withIssuedAt(new Date()).withExpiresAt(new Date(System.currentTimeMillis()+expirationMs)).sign(alg); } }
